package TypeCasting;

public class NarrowingTypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double d=127;
		
		float f=(float) d;
		
		long l=(long)f;
		
		int i =(int )l;
		
		char c=(char)i;
		
		short s=(short)c;
		
		byte b=(byte)s;
		
		
		System.out.println(d);
		System.out.println(f);
		System.out.println(l);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
